package poly.store.controller.user;

import java.util.List;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;

import poly.store.entity.Manufacturer;
import poly.store.entity.Product;
import poly.store.service.ManufacturerService;
import poly.store.service.ProductService;

/**
 * Class de hien thi trang thuong hieu
 *
 * @author phoenix-team
 * @version 1.00
 */
@Controller
public class ManufacturerController {

	@Autowired
	ManufacturerService manufacturerService;

	@Autowired
	ProductService productService;

	/**
	 * Hien thi danh sach cac thuong hieu
	 *
	 * @return trang manufacturers.html
	 */
	@GetMapping("/thuong-hieu")
	public String showManufacturers(Model model) {
		List<Manufacturer> manufacturers = manufacturerService.findAll();
		model.addAttribute("manufacturers", manufacturers);
		return "user/manufacturers";
	}

	/**
	 * Hien thi san pham theo thuong hieu
	 *
	 * @param id - ID cua thuong hieu
	 * @return trang manufacturer-detail.html
	 */
	@GetMapping("/thuong-hieu/{id}")
	public String showManufacturerDetail(@PathVariable("id") Integer id, Model model) {
		Manufacturer manufacturer = manufacturerService.findAll().stream()
				.filter(m -> m.getId() == id)
				.findFirst()
				.orElse(null);

		if (manufacturer != null) {
			List<Product> products = manufacturer.getListProduct();
			model.addAttribute("manufacturer", manufacturer);
			model.addAttribute("products", products);
			return "user/manufacturer-detail";
		}

		return "redirect:/thuong-hieu";
	}
}

